arguments.argument:
if clearScreenTimer > 0{
    exit;
}

if current_text != ""{
    ds_list_add(global.commandLog, current_text);
    while ds_list_size(global.commandLog) > MAX_COMMAND_LOG_SIZE{
        ds_list_delete(global.commandLog, 0);
    }
    commandLogIndex = ds_list_size(global.commandLog);
    console_print("/:/"+COLOR_ORANGE+current_text);
    console_parseInput(current_text);
}

current_text = ""
cursor_position = 0;
cursor_moving = 0;
PlayerControl.afktimer = PlayerControl.afktimeout// The host shouldn't get afk'ed for writing long stuff in the console.
