arguments.argument:
{
    menu_create(48, 172, 500, 260, 32, 40, 104, 6);
    menumode = true;

    if (room != DSMOptionsR)
        menu_setdimmed();

    menu_background(512, 24, 8, 12, 4);
    bgtabs = true;

    menu_addedit_key("Superburst:", "global.dsmSuperburst");
    menu_addedit_key("Quick X4:", "global.dsmQuickX4");
    menu_addedit_key("Show Walkmask:", "global.dsmShowWM");
    menu_addedit_key("Open/Close Console:", "global.dsmConsoleKey");
    menu_addedit_keyormouse("Alternate Screenshot:", "global.dsmScreenshot");

    menu_addtablink("DSM 1", '
        instance_destroy();
        instance_create(x,y,DSMOptionsController);
    ');
    menu_addtablink("DSM 2", '
        instance_destroy();
        instance_create(x,y,DSMOptionsController2);
    ');
    menu_addtablink("DSM 3", '
        instance_destroy();
        instance_create(x,y,DSMOptionsController3);
    ');
    menu_addtablink("Controls", '
        instance_destroy();
        instance_create(x,y,DSMControlsController);
    ');

    menu_addback("Back", '
        instance_destroy();
        if(room == DSMOptionsR)
            room_goto_fix(Menu);
        else
            instance_create(0,0,InGameMenuController);
    ');
}
