//dont allow the match to start if there are not at least [x] players on each team
if global.minPlayers!=-1 and global.forceReady!=1{
    var redTeam,blueTeam;
    redTeam=0
    blueTeam=0
    for (i=0; i<ds_list_size(global.players); i+=1){
        player=ds_list_find_value(global.players, i)
        if player.team==TEAM_RED{
            redTeam+=1
        }else if player.team==TEAM_BLUE{
            blueTeam+=1
        }
    }
    if redTeam<global.minPlayers{
        startingMatch=0
        exit;
    }
    if blueTeam<global.minPlayers{
        startingMatch=0
        exit;
    }
}

if global.readyTotal==global.readyMax{
    if global.readyTotal>0{
            startingMatch=1
    }
}

if global.readyMax==0{
    startingMatch=0
}
