arguments.argument:
{
    menu_create(48, 110, 500, 300, 32, 40, 104, 6)
    menumode = true;
    virnum=0
    setVirNum=0

    if (room != ModOptions){
        menu_setdimmed();
    }

    menu_background(512, 24, 8, 12, 4);
    bgtabs = true;

    menu_addedit_num("Volume:","global.volume",'
        es_write_ini("s","Volume",argument0)
        sound_global_volume(argument0/100)
    ')setVirNum+=1

    menu_addedit_boolean("Borderless Window:","global.borderless",'
        es_write_ini("s","Borderless",argument0)
        if argument0==1{
            window_set_showborder(0)
            window_set_showicons(0)
        }else if argument0==0{
            window_set_showborder(1)
            window_set_showicons(1)
        }
    ')setVirNum+=1

    menu_addedit_select("Medic Targeting:","global.medicTargeting",'
        es_write_ini("s","MedicTargeting",argument0)
    ')setVirNum+=1
    menu_add_option(0,"Line Of Sight (Original)")
    menu_add_option(1,"Cursor Positon (New)")

    menu_addedit_boolean("No Sounds During RUP:","global.noSoundsDuringRUP",'
        es_write_ini("s","NoSoundsDuringRUP",argument0)
    ')setVirNum+=1

    menu_addedit_boolean("Save Chat Logs:","global.writeChatLog",'
        es_write_ini("s","WriteChatLog",argument0)
    ')setVirNum+=1

    menu_addedit_boolean("Save Console Logs:","global.writeConsoleLog",'
        es_write_ini("s","WriteConsoleLog",argument0)
    ')setVirNum+=1

    //TABS
    menu_addtablink("Host", '
        instance_destroy()
        instance_create(x,y,ModHostOptionsController)
    ');
    menu_addtablink("GFX Basic", '
        instance_destroy()
        instance_create(x,y,ModOptGFXBasic)
    ');
    menu_addtablink("GFX Adv", '
        instance_destroy()
        instance_create(x,y,ModOptGFXAdv)
    ');
    menu_addtablink("Misc", '
        instance_destroy()
        instance_create(x,y,ModOptMisc)
    ');
    menu_addtablink("Controls", '
        instance_destroy()
        instance_create(x,y,ModControlsOptionsController)
    ');
    menu_addtablink("Configs", '
        instance_destroy()
        instance_create(x,y,ModConfigsController)
    ');

    menu_addback("[0] Back", '
        instance_destroy()
        if(room == ModOptions){
            room_goto_fix(Menu)
        }else{
            instance_create(0,0,InGameMenuController)
        }
    ');
}
