arguments.argument:
if(instance_exists(MenuController) or instance_exists(Console))
    exit;

var kickOpen;
kickOpen = false
if (instance_exists(ScoreTableController))
    if (ScoreTableController.showadmin)
        kickOpen = true;

if(instance_exists(TeamSelectController) || instance_exists(ClassSelectController) || kickOpen)
    menuOpen = true;
else
    menuOpen = false;

for (i = 0; i < ds_list_size(global.dsmBinds); i+=1){
    if real(ds_list_find_value(global.dsmBinds,i))==real(keyboard_key){
        if keyboard_check_pressed(keyboard_key){
            console_parseInput(ds_list_find_value(global.dsmBindCommands,i))
        }
    }
}

if keyboard_check_pressed(global.dsmScreenshot) or mouse_check_button_pressed(global.dsmScreenshot){
    takeScreenshot()
}

if keyboard_check_released(global.dsmChatHideButton){
    if global.chatHide==0{
        global.chatHide=1
        global.chatHideJustChanged=1
    }else if global.chatHide==1{
        global.chatHide=0
        global.chatHideJustChanged=1
    }
}

if keyboard_check_pressed(global.dsmReduceEyePain){
    //draw_background_ext(background_index[0],0,0,1,1,0,c_gray,1)
}

//Checking for input - Mapped Keys
if(keyboard_check_pressed(global.changeTeam))
    inputChangeTeam();
if(keyboard_check_pressed(global.changeClass))
    inputChangeClass();

event_user(8);

var keybyte;
keybyte = 0;

/* KeyByte flags:
    02 - down

    04 - medic call
    08 - primary
    10 - secondary

    20 - right
    40 - left
    80 - up
*/

//character object exists
if(global.myself.object != -1)
{
    if(!menuOpen)
    {
        if(keyboard_check(global.left)) keybyte |= $40;
        if(keyboard_check(global.right)) keybyte |= $20;
        if(keyboard_check(global.jump)) keybyte |= $80;
        if(keyboard_check(global.down)) keybyte |= $02;
        if(keyboard_check_pressed(global.medic)) keybyte |= $04;
        if(keyboard_check_pressed(global.chat1)) inputChat1();
        if(keyboard_check_pressed(global.chat2)) inputChat2();
        if(keyboard_check_pressed(global.chat3)) inputChat3();
        if(keyboard_check_pressed(global.drop)) inputDrop();

        //DSM
        if keyboard_check_pressed(global.dsmSuperburst){
            write_ubyte(global.serverSocket, CHAT_BUBBLE)
            write_ubyte(global.serverSocket, 46)
        }
        if(keyboard_check_pressed(global.dsmQuickX4)){
            write_ubyte(global.serverSocket, CHAT_BUBBLE)
            write_ubyte(global.serverSocket, 30)
        }

        if(!global.myself.humiliated)
        {
            if(keyboard_check(global.attack)) keybyte |= $10;
            if(keyboard_check(global.special)) keybyte |= $08;
            if(keyboard_check_pressed(global.special)) inputSpecial();
            if(keyboard_check_pressed(global.taunt))
            {
                keybyte |= $01;
                inputTaunt();
            }
            if(mouse_check_button(mb_left))
            {
                if(global.attack == MOUSE_LEFT) keybyte |= $10;
                if(global.special == MOUSE_LEFT) keybyte |= $08;
            }
            if(mouse_check_button_pressed(mb_left) and global.special == MOUSE_LEFT)
                inputSpecial();

            if(mouse_check_button(mb_right))
            {
                if(global.attack == MOUSE_RIGHT) keybyte |= $10;
                if(global.special == MOUSE_RIGHT) keybyte |= $08;
            }
            if(mouse_check_button(mb_middle))
            {
                if(global.attack == MOUSE_MIDDLE) keybyte |= $10;
                if(global.special == MOUSE_MIDDLE) keybyte |= $08;
            }
            if(mouse_check_button_pressed(mb_right) and global.special == MOUSE_RIGHT)
                inputSpecial();

        }
    }

    if(global.run_virtual_ticks or keyboard_check_pressed(global.taunt))
        ClientInputstate(global.serverSocket, keybyte);
    socket_send(global.serverSocket);
}
// spectator controls
else if (instance_exists(Spectator))
{
    if(!menuOpen)
    {
        if(mouse_check_button_pressed(mb_left))
            with (Spectator) event_user(7);
        if(mouse_check_button_pressed(mb_right))
            with (Spectator) event_user(8);
    }
}

if(keybyte != 0
        or keyboard_check(global.left)
        or keyboard_check(global.right)
        or keyboard_check(global.jump)
        or keyboard_check(global.down)) {
    afktimer = afktimeout;
}
