arguments.argument:
if(global.myself.object == -1) exit;

if(global.myself.class == CLASS_ENGINEER){
    if(global.myself.sentry and !instance_exists(SentryHealthHud)){
        instance_create(0,0,SentryHealthHud);
    }

    if(!instance_exists(NutsNBoltsHud)){
        instance_create(0,0,NutsNBoltsHud);
    }
}

if(global.myself.class == CLASS_HEAVY){
    if(!instance_exists(SandwichHud)){
        instance_create(0,0,SandwichHud);
    }


}else if (global.myself.class == CLASS_DEMOMAN) && !instance_exists(StickyCounter){
    // Sticky HUD. Because real men let computers count for them.
    instance_create(0,0,StickyCounter);
}else if global.myself.class == CLASS_MEDIC {
    //Uber HUD
    if !instance_exists(UberHud){
        instance_create(0,0,UberHud);
    }
    //Healing Hud
    if !instance_exists(HealingHud) && global.showHealing = 1{
        instance_create(0,0,HealingHud);
    }
    //Medic Radar
    if global.medicRadar == 1 && !instance_exists(MedicRadar){
        instance_create(0,0,MedicRadar);
    }
}else if global.myself.class==CLASS_QUOTE{
    if !instance_exists(SuperBubbleHud){
        instance_create(0,0,SuperBubbleHud)
    }
}

// Health HUD
if  !instance_exists(HealthHud){
    instance_create(0,0,HealthHud);
}

// Healed HUD
if !instance_exists(HealedHud) && global.showHealer = 1{
    instance_create(0,0,HealedHud);
}
