arguments.argument:
{
    cursor_sprite = CrosshairS;
    global.levelchoice = 1

    instance_create(576,80,currentPlugins)

    menu_create(40, 200, 200, 200, 32);
    menu_background(212, 24, 8, 12, 4);
    menu_setdimmed();

    switch global.consoleIsOpen{
        case true:
            instance_create(0, 0, Console);
            menu_addlink("Close Console", '
                console_close();
            ');
        break;

        case false:
            menu_addlink("Open Console", '
                console_open();
            ');
        break;
    }

    if (global.isHost){
        switch (global.recordingReplay){
            case false:
                menu_addlink("Record Replay", '
                beginRecording();
                instance_destroy() //recreate object to show player that they have pressed it
                instance_create(x,y,InGameMenuController);
                ');
            break;

            case true:
                menu_addlink("End Recording", '
                endRecording();
                instance_destroy()
                instance_create(x,y,InGameMenuController);
                ');
            break;
        }
    }

    menu_addlink("Return to Game", '
        instance_destroy();
    ');
    menu_addback("", '
        instance_destroy();
    ');
    /*if (global.isHost) {
        menu_addlink("Kick players", '
            if(!instance_exists(ScoreTableController)) instance_create(0,0,ScoreTableController);
            ScoreTableController.showadmin = true;
            instance_destroy();
        ');
    }*/

    menu_addlink("Options", '
        instance_destroy();
        instance_create(0,0,OptionsController);
    ');
    menu_addlink("DSM Options", '
        instance_destroy();
        instance_create(0,0,DSMOptionsController);
    ');
    menu_addlink("Stats", '
        instance_destroy();
        instance_create(0,0,StatsMenuController);
    ');
    menu_addlink("Lobby", '
        splash_set_main(false)
        splash_set_border(false)
        splash_set_interrupt(false)
        splash_set_size(800,600)
        splash_show_web("http://www.ganggarrison.com/lobby/status",0)
    ');
    menu_addlink("Disconnect", '
        if (global.recordingReplay)
        endRecording();

        addStats()
        // Force dedicated mode to off so you can go to main menu instead of just restarting server
        if (global.serverPluginsInUse)
        {
            pluginscleanup(true);
        }
        else
        {
            global.dedicatedMode = 0;
            with(Client)
                instance_destroy();

            with(GameServer)
                instance_destroy();
        }
    ');
    menu_addlink("Quit Game", '
        addStats()
        game_end();
    ');
}
