arguments.argument:
var i;
for(i =0; i < 9; i += 1) {
    if(mouse_x > xoffset[i] && mouse_x < xoffset[i] + 35) {
        if(mouse_y > yoffset[i] && mouse_y < yoffset[i] + 49) {
            if(mouse_check_button_pressed(mb_left) or
                mouse_check_button(mb_left) or
                mouse_check_button_released(mb_left)) {
                global.class = i;
            }
        }
    }
}

if (keyboard_check_pressed(ord('Q'))) {
    global.class = CLASS_QUOTE;
}
