arguments.argument:
{
    menu_create(40, 150, 200, 200, 30);
    menu_setdimmed();

    menu_addlink("Give full health", '
        if(global.myself.object != -1) {
            global.myself.object.hp = global.myself.object.maxHp;
        }
    ');
    menu_addedit_boolean("God mode", "CheatController.god", '');
    menu_addedit_boolean("Invisible", "CheatController.notarget", '');
    menu_addedit_boolean("Infinite ammo", "CheatController.infiniteAmmo", '');
    menu_addedit_boolean("Infinite metal", "CheatController.infiniteMetal", '');
    menu_addedit_boolean("Infinite ubercharge", "CheatController.infiniteCharge", '');
    // If you don't toggle the uber off in the menu, it won't run out unless you get healed by a medic.
    menu_addedit_boolean("Always ubered", "CheatController.alwaysUbered", '
        global.myself.object.ubered = false;
    ');
    menu_addedit_boolean("1 hit kill", "CheatController.oneHitKill", '');
    menu_addlink("", '');
    menu_addback("Back", '
        instance_destroy();
        instance_create(x,y,InGameMenuController);
    ')
}
