arguments.argument:
menu_create(48, 172, 500, 260, 32, 40, 104, 6);
menu_background(512, 24, 8, 12, 4);
bgtabs = true;


var i, time, timeStr, minStr, secStr, millisStr;
for (i = 0; i < 10; i+=1) {
    time = TrainingMapGetHiscore(global.currentMap, i);
    if (time == noone) {
        timeStr = "--:--:--";
    } else {
        minStr = string(floor(time / (30 * 60)));
        secStr = string(floor((time / 30) mod 60));
        millisStr = string(floor((time) mod (30 * 60)));
        if (string_length(secStr) < 2) {
            secStr = "0" + secStr;
        }
        if (string_length(millisStr) < 2) {
            millisStr = "0" + millisStr;
        }
        secStr = string_copy(secStr, 1, 2);
        millisStr = string_copy(millisStr, 1, 2);
        timeStr = minStr + ":" + secStr + ":" + millisStr;
    }
    menu_addlink(classname(i) + " : " + timeStr, '');
}

menu_addback("Back", '
    instance_create(x,y,MapHiscoreMenuController);
    instance_destroy();
');
