arguments.argument:
xoffset = view_xview[0];
yoffset = view_yview[0];
xsize = view_wview[0];
ysize = view_hview[0];
if (global.kothHudPos)
{
    coffset = 0;
    xshift = -323*global.timerPos;
    yshift = 35;
}
else
{
    coffset = 43*1.15;
    xshift = -290*global.timerPos;
    yshift = 0;
}

draw_set_color(c_white);
draw_set_valign(fa_center);
draw_set_alpha(1);

//CP
if (KothControlPoint.team == TEAM_RED)
    teamoffset = 60;
else if (KothControlPoint.team == TEAM_BLUE)
    teamoffset = 90;
if (KothControlPoint.cappingTeam == TEAM_RED and KothControlPoint.team == -1)
    teamoffset = 30;
else if (KothControlPoint.cappingTeam != TEAM_RED && KothControlPoint.team == -1)
    teamoffset = 0;

if (KothControlPoint.capping != 0){
    draw_sprite_ext(ControlPointStatusS, teamoffset+floor(KothControlPoint.capping/KothControlPoint.capTime*30),
                    xoffset+xsize/2, yoffset+ysize-40, 3, 3, 0, c_white, 1);
    if global.dsmHUDs==1{
        var cpCapPercent;
        cpCapPercent = string(floor(KothControlPoint.capping/KothControlPoint.capTime*100)) + "%"
        draw_set_halign(fa_center)
        draw_set_valign(fa_center)
        draw_set_color(c_black)
        draw_text_transformed((xoffset+xsize/2)+4, yoffset+572, cpCapPercent,1,1,0)
    }
}else if (KothControlPoint.capping == 0){
    draw_sprite_ext(ControlPointStatusS, teamoffset, xoffset+xsize/2, yoffset+ysize-40, 3, 3, 0, c_white, 1);
}

if (KothControlPoint.locked){
    if global.dsmHUDs==1{
        draw_set_halign(fa_center)
        if cpUnlock >= 1 draw_sprite_ext(ControlPointLockS, 0, xoffset+xsize/2, yoffset+ysize-40, 3, 3, 0, c_white, 1)
        draw_set_valign(fa_center)
        if cpUnlock >= 900 {} //To prevent display problems at server join and during set-up
        else if ceil(cpUnlock/30) > 5 draw_text_transformed(xoffset+xsize/2+2, yoffset+ysize-38, ceil(cpUnlock/30), 1.0, 1.0, 0);
        else if ceil(cpUnlock/30) <= 5 draw_text_transformed(xoffset+xsize/2+2, yoffset+ysize-38, ceil(cpUnlock/30), 3,3, 0);
    }else{
        draw_set_halign(fa_center);
        if (cpUnlock >= 150){
            draw_sprite_ext(ControlPointLockS, 0, xoffset+xsize/2, yoffset+ysize-40, 3, 3, 0, c_white, 1);
        }else if (cpUnlock >= 30){
            draw_text_transformed(xoffset+xsize/2+2, yoffset+ysize-38, ceil(cpUnlock/30), 3,3, 0);
        }
    }
}else if (KothControlPoint.cappers > 0 and !KothControlPoint.locked){
    if global.dsmHUDs==1{
        draw_sprite_ext(ControlPointCappersS, 0, xoffset+xsize/2, yoffset+ysize-45, 3, 3, 0, c_white, 1);
        draw_set_halign(fa_center);
        draw_set_valign(fa_center);
        draw_set_color(c_black);
        draw_text_transformed(xoffset+xsize/2+13, yoffset+ysize-43, string(KothControlPoint.cappers),1.5,1.5,0);
    }else{
        draw_sprite_ext(ControlPointCappersS, 0, xoffset+xsize/2, yoffset+ysize-40, 3, 3, 0, c_white, 1);
        draw_set_halign(fa_center);
        draw_set_valign(fa_center);
        draw_set_color(c_black);
        draw_text_transformed(xoffset+xsize/2+13, yoffset+ysize-37, string(global.cp[i].cappers),1.5,1.5,0);
    }
}

draw_kothtimer();
