arguments.argument:
if instance_exists(Console) exit;
if ChatBox.isTyping!=false exit;

//if balance == TEAM_RED team = TEAM_BLUE;
//else if balance == TEAM_BLUE team = TEAM_RED;
//else if balance == -1 team = choose(TEAM_RED,TEAM_BLUE);

var redTeam,blueTeam;
redTeam=0
blueTeam=0
for (i=0; i<ds_list_size(global.players); i+=1){
    player=ds_list_find_value(global.players, i)
    if player.team==TEAM_RED{
        redTeam+=1
    }else if player.team==TEAM_BLUE{
        blueTeam+=1
    }
}
if redTeam>blueTeam{
    team=TEAM_BLUE
}else if blueTeam>redTeam{
    team=TEAM_RED
}else{
    if global.myself.team!=TEAM_RED or global.myself.team!=TEAM_BLUE{
        team=choose(TEAM_RED,TEAM_BLUE)
    }
}
event_user(1);
