arguments.argument:
if alpha==1{ //Don't want to accidently press buttons.
    if !(mouse_y > y and mouse_y < y+60) exit

    if mouse_x > x+stopButton[0] and mouse_x < x+stopButton[1]{
        // User pressed the stop button
        if show_question("Are you sure you want to exit the replay?"){// and wants to quit
            with Client{
                instance_destroy();
            }
        }
    }else if mouse_x > x+playButton[0] and mouse_x < x+playButton[1]{
        // User pressed the play button
            global.replaySpeed=1
    }else if mouse_x > x+fastButton[0] and mouse_x < x+fastButton[1]{
        // User pressed the fast-forward button
        if global.replaySpeed<=6{ //We don't want it to go too fast.
            global.replaySpeed += 1
        }
    }
}
