arguments.argument:
if(global.myself.object != -1) {
    if(god) {
        global.myself.object.hp = 255;
    }
    if(notarget) {
        global.myself.object.uncloakTime = 0;
    }
    if(infiniteAmmo) {
        with(global.myself.object.currentWeapon) {
            ammoCount = maxAmmo;
        }
    }
    if(infiniteMetal) {
        global.myself.object.nutsNBolts = global.myself.object.maxNutsNBolts;
    }
    if(infiniteCharge) {
        var myMedigun;
        myMedigun = noone;
        with(Medigun) {
            if(ownerPlayer == global.myself) myMedigun=id;
        }
        if(myMedigun != noone) {
            myMedigun.uberCharge = 2000;
        }
    }
    if(alwaysUbered) {
        global.myself.object.ubered = true;
    }
    if(oneHitKill) {
        with(Character) {
            if(hp != maxHp) {
                if(timeUnscathed < 2) {
                    hp = 0;
                }
            }
        }
    }
}
