arguments.argument:
var xoffset, yoffset;
var xoffsetFace, yoffsetFace;
var xoffsetTxt, yoffsetTxt;
var xoffsetSprite, yoffsetSprite;
var xsizeSprite, ysizeSprite;
var boxw, boxh;

xoffset = view_xview[0] + 60;
yoffset = view_yview[0] + view_hview[0] - 164;
boxw = 64;
boxh = 64;
xoffsetFace = view_xview[0] + 60 + boxw/2;
yoffsetFace = view_yview[0] + view_hview[0] - 164 + boxh/2;
xoffsetTxt = view_xview[0] + 60 + boxw + 10;
yoffsetTxt = view_yview[0] + view_hview[0] - 144;


draw_set_alpha(alpha);
draw_set_color(c_white);
draw_set_halign(fa_left);
draw_set_valign(fa_top);

draw_sprite_ext(sprite_index, 0, xoffset, yoffset, 4, 4, 0, dialogMsg.color, alpha * 0.7);

if(dialogMsg.face != noone) {
    xoffsetSprite   = sprite_get_xoffset(dialogMsg.face);
    yoffsetSprite   = sprite_get_yoffset(dialogMsg.face);
    xsizeSprite     = sprite_get_width(dialogMsg.face);
    ysizeSprite     = sprite_get_height(dialogMsg.face);

    maxScale = min(boxw / xsizeSprite, boxh / ysizeSprite);

    draw_sprite_ext(dialogMsg.face, dialogMsg.subFace,
        xoffsetFace + xoffsetSprite - xsizeSprite / 2, yoffsetFace + yoffsetSprite - ysizeSprite / 2,
        maxScale, maxScale, 0, c_white, alpha * 0.9);
}

draw_text_transformed(xoffsetTxt, yoffsetTxt, dialogMsg.text, 1, 1, 0);
