arguments.argument:
alarm[0] = 45 / global.delta_factor;
alarm[1] = 105 / global.delta_factor;

AudioControlPlaySong(-1);
if(sound_exists(global.playerDeathSound)) {
    sound_play(global.playerDeathSound);
}
global.respawnTime = 9001;

text = "GAME OVER#You are dead."; // default text.
fadeAlpha = 0;
fading = false;

xRestart = view_xview[0] + (view_wview[0] / 2) - 100;
xQuit    = view_xview[0] + (view_wview[0] / 2) + 100;
yButtons = view_yview[0] + view_hview[0] - 240;

with(Player) {
    canSpawn = 0;
    alarm[5] = -1;
    if(team == global.myself.team) {
        humiliated = 1;
        with(sentry) {
            event_user(1);
        }
    } else {
        keybyte |= $01;
    }
}
with(NPC) {
    npcSetTask(NPC_TASK_IDLE);
}
