arguments.argument:
alarm[0] = 45;
alarm[1] = 105;

AudioControlPlaySong(-1);
if(sound_exists(global.playerDeathSound)) {
    sound_play(global.playerDeathSound);
}
global.respawnTime = 9001;

text = "GAME OVER#You are dead."; // default text.
fadeAlpha = 0;
fading = false;

with(Player) {
    canSpawn = 0;
    alarm[5] = -1;
    if(team == global.myself.team) {
        humiliated = 1;
        with(sentry) {
            event_user(1);
        }
    } else {
        keybyte |= $01;
    }
}
with(NPC) {
    npcSetTask(NPC_TASK_IDLE);
}
