arguments.argument:
var xoff, yoff, xsize, ysize;

xoff    = view_xview[0];
yoff    = view_yview[0];
xsize   = view_wview[0];
ysize   = view_hview[0];

// black fading
draw_set_alpha(fadeAlpha);
draw_set_color(c_black);
draw_rectangle(xoff, yoff, xoff + xsize, yoff + ysize, false);

// game over text
draw_set_valign(fa_center);
draw_set_halign(fa_middle);
draw_set_color(c_white);
draw_text(xoff + xsize / 2, yoff + ysize / 2, text);

// button/links
if ((abs(mouse_y - yButtons) < 40) and (abs(mouse_x - xRestart) < 80)) {
    draw_set_color(c_red);
} else {
    draw_set_color(c_white);
}
draw_text(xRestart, yButtons, "Restart");

if ((abs(mouse_y - yButtons) < 40) and (abs(mouse_x - xQuit) < 80)) {
    draw_set_color(c_red);
} else {
    draw_set_color(c_white);
}
draw_text(xQuit, yButtons, "Back to menu");
