arguments.argument:
if(object != -1) {
    justJumped = max(0, justJumped - 1);
    keybyte = 0;
    aimDirection = 0;
    aimDistance = 60;

    jump = 0;
    LMB = 0;
    RMB = 0;

    npcRunAI();
    npcRunAim();

    if(jump > 0 && justJumped <= 0) {
        keybyte |= $80;
        justJumped = 10;
    }
    if(dir == -1) {
        keybyte |= $40; // left
    } else if(dir == 1) {
        keybyte |= $20; // right
    }
    if(LMB > 0) keybyte |= $10;
    if(RMB > 0) keybyte |= $08;

    object.keyState = keybyte;
    object.aimDirection = aimDirection;
    object.aimDistance = aimDistance;
} else {
    reloadCounter = 0;
}
