arguments.argument:
_sprites_index_list = ds_list_create();
_sounds_index_list = ds_list_create();

var i, sprite, sound;

for(i = 0; i < ds_list_size(_sprites_index_list); i+=1) {
    sprite = ds_list_find_value(_sprites_index_list, i);
    sprite_delete(sprite);
}
for(i = 0; i < ds_list_size(_sounds_index_list); i+=1) {
    sound = ds_list_find_value(_sounds_index_list, i);
    sound_delete(sound);
}

ds_list_destroy(_sprites_index_list);
ds_list_destroy(_sounds_index_list);

ds_map_destroy(sprites);
ds_map_destroy(sounds);
