arguments.argument:
show_message("Creating menu");
with(PlayerControl) instance_destroy();

destroyEventDone = false;   // gotta do that because destroying the instance
            // causes a room changes, which destroys the instance again...

// CHAT INIT
chatLog = ds_list_create();
chatOffset = 0;

// PLAYABLE SLOTS
// TODO: get playable slots from map
slots = ds_list_create(); // list of strings

// REPLACE PLAYER LIST
var p, i;
list = ds_list_create();
for(i = 0; i < ds_list_size(global.players); i += 1) {
    with(ds_list_find_value(global.players, i)) {
        if(object_index == Player) {
            p = instance_create(x,y,SetupPlayer);
            p.socket = socket;
            p.name = name;
            p.rewards = rewards;
            p.ghost = ghost;
            socket = -1; // don't destroy me :(
            rewards = -1; // me neither :(
            ghost = noone; // and me plz :(
            ds_list_add(other.list, p);
            if(global.myself == id) global.myself = p; // replace myself
        }
        instance_destroy();
    }
}
ds_list_destroy(global.players);
global.players = list;


// MENUS
nbMenus = 3
