arguments.argument:
var i;

for(i = 0; i < nbMenus; i+=1) {

    if(menuX[i] < mouse_x and mouse_x < menuX[i] + string_width(menuText[i])) {
        if(menuY[i] < mouse_y and mouse_y < menuY[i] + string_height(menuText[i])) {
            if(mouse_check_button_pressed(mb_left)) {
                execute_string(menuScript[i]);
            }
        }
    }

}
